import React, { useState, useEffect } from "react";
//import { Modal } from 'react-bootstrap'
import Modal from "./Components/Modal"
import Axios from 'axios';
import './App.css';

function App() {

  const [courseCode, setCourseCode] = useState('')
  const [courseName, setCourseName] = useState('')
  const [courseList, setCourseList] = useState([])
  const [showModal, setShowModal] = useState(false)

  useEffect(()=> {
    Axios.get('http://localhost:4000/api/get').then((response)=> {
      setCourseList(response.data)
      console.log(response.data)
    })
  }, [])

  const submitCourse = () => {
    Axios.post('http://localhost:4000/api/insert', {
      courseCode: courseCode,
      courseName: courseName,
    }).then(() => {
      setShowModal(false)
      alert("Successful insert")
    })
  }

  return (
    <div className="App">
      <h1>Course Scores App</h1>
      <button className="openModalBtn" onClick={() => {setShowModal(true)}}>Add Course</button>

      <Modal />
      {/* {showModal && (
        <Modal>
          <div className="modal-form">
            <label>Course Code:</label>
            <input type="text" name="courseCode" onChange = {(e) => {
              setCourseCode(e.target.value)
            }}
            />
            <input type="text" name="courseName" onChange = {(e) => {
              setCourseName(e.target.value)
            }}
            />

            <button onClick={submitCourse}>Submit</button>
            <button onClick={() => setShowModal(false)}>Cancel</button>
          </div>
        </Modal>
      )} */}

      <div className="form">
        {/* <button onClick={() => setShowModal(true)}>Add Course</button> */}
        <button className="openModalBtn">Add Course</button>

        {courseList.map((val)=> {
          return ( 
            <h2>
              Course Code: {val.courseCode} |  Course Name: {val.courseName}
            </h2>
          )
        })}
      </div>
    </div>
  )
}

export default App;
